ascript" src="{pigcms{$static_public}js/jquery.fancybox-1.3.1.pack.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/layer/layer.min.js"></script><script type="text/javascript">$(function(){	try {	$("a[rel=show_img]").fancybox({		'titlePosition' : 'over',		'cyclic'		: false,	});	} catch (e) {			}	$(".js-express").live("click", function () {		if ($(".js-express_detail").data("type") != "default") {			return;		}				var express_code = $(this).data("express_code");		var express_no = $(this).attr("data-express_no").toString();				$(".js-express_detail").html("tr><td></td><td>努力查询中,请稍等</td></tr>");		var url = "wap/express.php?type=" + express_code + "&express_no=" + express_no + "&" + Math.random();		$.getJSON(url, function(data) {			try {				if (data.status == true) {					html = '';					for(var i in data.data.data) {						html += '<tr>';						html += '	<td style="text-align:right; padding-right:10px; height:24px; line-height:24px;">' + data.data.data[i].time + '</td>';						html += '	<td>' + data.data.data[i].context + '</td>'						html += '</tr>';					}					$(".js-express_detail").html(html);					$(".js-express_detail").data("type", "data");				} else {					html += "tr><td></td><td>查询失败</td></tr>";					$(".js-express_detail").html(html);				}			}catch(e) {				html += "tr><td></td><td>查询失败</td></tr>";				$(".js-express_detail").html(html);			}		});	});})</script><div class="section">	<h2 class="section-title clearfix">		退货详情	</h2>	<div class="section-detail clearfix">		<table>			<tbody>				<tr>					<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;状态：</td>					<td><?php echo $return['status_txt'] ?></td>				</tr>				<tr>					<td>下单用户：</td>					<td><?php echo $return['nickname'] ?> <?php echo $return['phone'] ?></td>				</tr>				<tr>					<td>维权类型：</td>					<td><?php echo $return['type_txt'] ?></td>				</tr>				<tr>					<td>维权理由：</td>					<td><?php echo nl2br(htmlspecialchars($return['content'])) ?></td>				</tr>				<tr>					<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;图片：</td>					<td>						<?php 						if (is_array($return['images'])) {							foreach ($return['images'] as $tmp) {						?>								<a href="<?php echo $tmp ?>" rel="show_img">									<img src="<?php echo $tmp ?>" style="max-width:60px; max-height:60px; float:left; padding-right:5px;">								</a>						<?php							}						} else {						?>							暂无图片						<?php						}						?>					</td>				</tr>			</tbody>		</table>	</div>	<h2 class="section-title clearfix">		审核信息	</h2>	<div class="section-detail clearfix">		<table>			<tbody>				<?php 					if ($return['cancel_dateline']) {					?>						<tr>							<td>不同意说明</td>							<td><?php echo htmlspecialchars($return['store_content']) ?></td>						</tr>					<?php					}					if (!empty($return['address_user'])) {						$address = unserialize($return['address']);					?>						<tr>							<td>收货信息</td>							<td><?php echo htmlspecialchars($return['address_user']) ?> <?php echo $return['address_tel'] ?></td>						</tr>						<tr>							<td>收货地址</td>							<td><?php echo $address['province_txt'] . $address['city_txt'] . $address['area_txt'] . $address['address'] ?></td>						</tr>						<tr>							<td>退货金额</td>							<td>￥<?php echo sprintf('%.2f', $return['product_money'] + $return['postage_money']) ?> = 退货产品金额：<?php echo $return['product_money'] ?> + 退货物流金额：<?php echo $return['postage_money'] ?></td>						</tr>					<?php 					}					if (empty($return['cancel_dateline']) && empty($return['address_user'])) {					?>						<tr>							<td></td>							<td>暂无审核信息</td>						</tr>					<?php					}					?>			</tbody>		</table>	</div>	<?php 	if ($return['express_no']) {	?>		<h2 class="section-title clearfix">			物流信息		</h2>		<div class="section-detail clearfix">			<div>				<table>					<tbody>						<tr>							<td style="width:150px; text-align:right;">物流公司：</td>							<td>								<?php echo $return['express_company'] ?>&nbsp;&nbsp;&nbsp;&nbsp;物流单号：<?php echo $return['express_no'] ?>								<button class="js-express" data-express_no="<?php echo $return['express_no'] ?>" data-express_code="<?php echo $return['express_code'] ?>">查看物流</button>							</td>						</tr>					</tbody>					<tbody class="js-express_detail" data-type="default">					</tbody>				</table>			</div>		</div>	<?php 	}	?></div><div class="section">	<h2 class="section-title clearfix">		供货商、分销商利润列表	</h2>	<div class="section-detail clearfix">		<table class="table order-goods">			<thead>				<tr>					<td></td>					<td>店铺名称</td>					<td>联系人</td>					<td>联系电话</td>					<td>利润</td>				</tr>			</thead>			<?php 			foreach ($store_list as $store) {			?>				<tr>					<td><img src="<?php echo $store['logo'] ?>" style="max-width:60px; max-height:60px;" /></td>					<td>						<?php echo htmlspecialchars($store['name']) ?>						<?php 						if ($store['is_fx'] == 1) {						?>							<br /><span style="background:#f60; padding:2px 7px; color:white; border-radius:2px;"><?php echo $store['drp_level'] ?>级分销商</span>						<?php						} else if ($store['is_fx'] == 2) {						?>							<br /><span style="background-color:#07d; padding:2px 7px; color:white; border-radius:2px;">经销商</span>						<?php 						} else {						?>							<br /><span style="background:#f60; padding:2px 7px; color:white; border-radius:2px;">供货商</span>						<?php						}						?>					</td>					<td><?php echo htmlspecialchars($store['linkman']) ?></td>					<td><?php echo $store['service_tel'] ?></td>					<td><?php echo sprintf('%.2f', $store['profit']) ?></td>				</tr>			<?php			}			?>		</table>	</div></div><div class="section">	<h2 class="section-title clearfix">		维权产品信息 订单号：{pigcms{$return.order_no}	</h2></div><table class="table order-goods">	<thead>	<tr>		<th></th>		<th>商品名称</th>		<th>单价（元）</th>		<th>数量</th>		<th>小计（元）</th>		<th>维权类型</th>		<th>维权状态</th>	</tr>	</thead>	<tbo